class Solution {
public:
    int maxOperations(string s) {
        int ct = 0;
        int n = s.size();
        int ans = 0;
        bool f = 1;
        for(int i=0;i<n;i++){
            if(s[i]=='0'){
                if(f){
                 ans+=ct;f=0;}
            }
            else{
                ct++;
                f=1;
            }
        }
        return ans;
    }
};
