class Solution {
public:
    int maxOperations(string s) 
    {  
     int n = s.size();
     int i = 0 , ans = 0, cnt = 0;
     while( i < n )
     {
        if( s[i] == '1' )
        cnt++;
        if( i < n - 1 && s[i] == '1' && s[i+1] == '0' )
        ans += cnt;
        i++;
     }

     return ans;
    }
};
