#include <iostream>
#include <bits/stdc++.h>
using namespace std;
class Solution
{
public:
    int maxOperations(string s)
    {
        int n = s.size();
        int cnt = 0;
        int i = 0, j = 0;
        int prevcnt = 0;
        while (i < n)
        {
            if (s[i] == '1')
            {
                j = i + 1;
                while (j < n && s[j] == '0')
                {
                    j++;
                }
                if (j - 1 != i)
                {
                    i = j - 1;
                    cnt++;
                    cnt += prevcnt;
                }
                else
                {
                    i++;
                }
                prevcnt++;
            }
            else
            {
                i++;
            }
        }
        return cnt;
    }
};
