class Solution {
public:
    int maxOperations(string s) {
       vector<int> no;
        int ans = 0;
        int i =s.length()-1;
        if(s[i]!= '1')
        {
            no.push_back(i);
        }
        while(i>=0)
        {
            if(s[i] == '1')
            {

                no.push_back(i);
                while(i>=0 && s[i] == '1')
                {
                ans = ans+no.size()-1;
                    i--;
                }
            }
            else
            {
                i--;
            }
        }

        return ans;
    }
};
