class Solution {
public:
    int maxOperations(string s) {
        int ret = 0;
        int ones = 0;
        int mid_zeros = 0;
        for (char c : s) {
            if (c == '1') {
                if (mid_zeros > 0) {
                    ret += ones;
                    mid_zeros = 0;
                }
                ones++;
            } else {
                mid_zeros++;
            }
        }
        if (mid_zeros > 0) {
            ret += ones;
        }
        return ret;
    }
};
