class Solution {
    public int maxOperations(String s) {
        int ct = 0;
        int n = s.length();
        int ans = 0;
        for (int i = 0, len = 0; i < n - 1; i++) {
            int cur = s.charAt(i) - '0';
            if (cur == 1) {
                len++;
                if (s.charAt(i+1) == '0') {
                    ans += len;
                }
            }
        }
        return ans;
    }
}
