class Solution {
public:
    int maxOperations(string s) {
        int n = s.length();
        long long ans = 0, tmp = 0;
        for(int i = 0; i < n; i++)
            if(i == n - 1) break;
            else {
                if(s[i] == '1') tmp += 1;
                if(s[i] == '1' && s[i + 1] == '0') {
                    int j = i + 1;
                    for(j = i + 1; j < n; j++) 
                        if (s[j] == '1') {
                            break;
                        }
                    i = j - 1;
                    ans += tmp;
                }
            }
        return ans;
    }
};
