class Solution:
    def maxOperations(self, s: str) -> int:

        s=list(s)
        s=s[::-1]
        count_0=0
        r=0
        res=0

        while r<len(s):
            if s[r]=="1":
                res+=count_0
                r+=1
                continue

            count_0+=1
            while r<len(s) and s[r]=="0":
                r+=1

        return res
