class Solution {
public:
    int maxOperations(string s) 
    {
        int n=s.size();
        int j=0,ans{};

        if(s[0]=='1')
        {
            j++;
        }

        for(int i{1};i<n;i++)
        {
            if((s[i]=='1'&&s[i-1]=='0')||(s[i]=='0'&&i==n-1))
            {
                ans+=(j);
                j++;
            }
            else if(s[i]=='1')
            {
                j++;
            }
        }

        return ans;
    }
};
