class Solution {
public:
    int maxOperations(string s) {
        int ans =0;
        int cnt=0;
        for(int i=s.size()-1;i>=0;){
            if(s[i]=='0'){
                cnt++;
            }
            while(i>=0&&s[i]=='0'){
                i--;
            }
            if(i>=0&&s[i]=='1'){
                ans+=cnt;
                i--;
            }
        }
        return ans;
    }
};
