class Solution {
public:
    int maxOperations(string s) {
        std::string str="";
        for(auto x:s){
            if(str.empty()) str+=x;
            else if(str.back()!=x) str+=x;
            else if(str.back()=='1') str+=x;
        }
        long long res=0,cnt=0;
        for(auto x:str){
            if(x=='0') res+=cnt;
            else cnt++;
        }
        return res;
    }
};
