class Solution {
public:
    int maxOperations(string s) {
        int ops = 0;
        int onc = 0;
        for(int i=0;i<s.size()-1;i++){
            if(s[i] == '1'){
                onc++;
            }
            if(s[i] == '1' && s[i+1] == '0'){
                ops += onc;
            }
        }
        return ops;
    }
};
