class Solution {
    public int maxOperations(String s) {
        int count = 0, res = 0, n = s.length(), i = 0;
        while (i < n) {
            if (s.charAt(i) == '0') {
                res += count;
                int j = i + 1;
                while (j < n && s.charAt(j) == '0') {
                    j++;
                }
                i = j;
            } else {
                count++;
                i++;
            }
        }
        return res;
    }
}
