class Solution {
    public int maxOperations(String s) {
        long res = 0;
        int cnt = 0;
        char pre = '2';
        for(char c : s.toCharArray()) {
            if(c == '1') cnt++;
            else if(pre == '1')
                res += cnt;
            pre = c;
        }
        return (int)res;
    }
}
