import java.util.Arrays;

class Solution {
    public int maxOperations(String s) {
        int cum = s.charAt(0) - '0';
        int ans = 0;

        for (int i = 1; i < s.length(); i++) {
            if (s.charAt(i) == '1')
                cum++;
            else if (s.charAt(i - 1) != '0')
                ans += cum;

            tr(i, cum, ans);
        }
        return ans;
    }

