class Solution {
    public int maxOperations(String s) {
        int ans = 0;
        char[] str = s.toCharArray();
        int n = str.length;

        int ones = 0;
        boolean first = true;
        for (int i = 0; i < n; i++)  {
            if (str[i] == '1') {
                ones++;
                first = true;
            }
            if (str[i] == '0' && first) {
                first = false;
                ans += ones;
            }
        }

        return ans;
    }
}
