class Solution:
    def maxOperations(self, s: str) -> int:
        ans = 0
        count = 0

        n = len(s)
        i = 0
        while i < n:
            char = s[i]
            if char == '1':
                count += 1
            elif char == '0':
                while i+1 < n and s[i+1] == '0':
                    i += 1
                ans += count
            i += 1

        return ans
