class Solution {
public:
    int maxOperations(string s) {
        int n = s.size(), res = 0, ones = 0;

        for (int i = 0; i < n;) {
            if (s[i] == '1') {
                ++ones, ++i;
                continue;
            }
            int j = i;
            while (j < n && s[j] == '0') ++j;
            res += ones;
            i = j;
        }
        return res;
    }
};
