class Solution:
    def maxOperations(self, s: str) -> int:

        if '1' not in s:
            return 0
        n = len(s)
        for i,x in enumerate(s):
            if x == '1':
                idx = i
                break

        l = []
        cnt = 0
        for i in range(idx + 1,n):
            if s[i] == '0':
                cnt += 1
            else:
                l.append(cnt)
                cnt = 0
        l.append(cnt)

        ans = 0
        for j,x in enumerate(l):
            if x != 0:
                ans += j + 1
        return ans
