class Solution {
public:
  int maxOperations(string s) {
    int n = s.length();

    vector<int> f(n);

    for (int i = n-2; i >= 0; --i) {
      if (s[i] == '1' && s[i+1] == '0') f[i] = f[i+1] + 1;
      else f[i] = f[i+1];
    }

    int res = 0;
    for (int i = 0; i < n; ++i) if (s[i] == '1') res += f[i];
    return res;
  }
};
