class Solution {
public:
    int maxOperations(string s) {
        reverse(s.begin(), s.end());
        s += '0';
        char state = '1';
        int round = 0;
        int cnt = 0;
        int curr = 0;
        for (char c: s) {
            if (state == '1' && c == '1') {
                curr += 1;
            } else if (state == '1' && c == '0') {
                cnt += curr * round;
                round += 1;
                curr = 0;
            } else if (state == '0' && c == '0') {
                continue;
            } else {
                curr += 1;
            }

            state = c;
        }

        return cnt;
    }
};
