class Solution {
public:
    int maxOperations(string s) {
        int l = s.length();
        int temp = 0;
        int res = 0;
        for(int i=0; i<l-1; ++i) {
            if(s.at(i) == '1') {
                temp ++;
                if(s.at(i+1) == '0') {
                    res += temp;
                }
            }
        }
        return res;
    }
};
