class Solution {
public:
    int maxOperations(string s) {
        int res=0,op=0;
        int n=s.size();

        for(int i=n-2;i>=0;i--){
            if(s[i]=='0'){
                 continue;
            }
            else if(s[i]=='1'){
                if(s[i]==s[i+1])
                res+=op;
                else {
                op++;
                res+=op;
                }
            }
        }
        return res;
    }
};
