class Solution {
public:
    int maxOperations(string s) {
        int n = s.size();

        int ans = 0;
        int cnt = 0;
        int red = 0;
        for(int i=0;i<n;i++) {
            if(s[i] == '1') {
                red = 1;
                cnt++;
            }
            if(s[i] == '0'  &&  red == 1) {
                ans += cnt;
                red = 0;
            }
        }

        return ans;

    }
};
