class Solution {
    public int maxOperations(String s) {
        int cnt = 0;
        int ans = 0;
       int i = 0;
        while(i<s.length()){
            if(s.charAt(i)=='1'){
                cnt++;
            }else{
                ans+=cnt;
               // System.out.println("moved i from "+i+" "+cnt);
                while(i<s.length() && s.charAt(i)=='0'){
                    i++;

                }
                continue;
            }

            i++;
        }
        return ans;
    }
}
