class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int counter = 0;
        bool state=false;
        for(auto& c:s)
        {
            if(c=='0')
            {
                if(state)
                {
                    ans+=counter;
                    // counter=0;
                    state=false;
                }
            }
            else
            {
                state=true;
                counter++;
            }
        }
        return ans;
    }
};
