class Solution:
    def maxOperations(self, s: str) -> int:
        if len(s) == 1:
            return 0

        oneCt = 0
        totalMoves = 0 

        curNum = s[0]
        if curNum == "1":
            oneCt += 1

        for i in range(1, len(s)):
            if curNum != s[i]:
                if curNum == "1": # 1 -> 0
                    curNum = "0"
                    totalMoves += oneCt
                else: # 0 -> 1
                    curNum = "1"
                    oneCt += 1
            elif s[i] == "1":
                oneCt += 1


        return totalMoves
