class Solution:
    def maxOperations(self, s: str) -> int:
        zero_runs = ans = 0
        run = False
        for i in range(len(s) - 1, -1, -1):
            if s[i] == '0':
                if not run:
                    run = True
                    zero_runs += 1
            else:
                run = False
                ans += zero_runs
        return ans
