class Solution {
public:
    int maxOperations(string s) {
        int n=s.size(), i=0, cnt=0, ans=0;
        while(i<n){
            if(s[i] == '1') cnt++;
            else{
                ans += cnt;
                while(i<n && s[i] == '0')i++;
                cnt++;
            }
            i++;
        }
        return ans;
    }
};
