class Solution {
public:
    int maxOperations(string s) {
        int res = 0;
        int n = s.size();
        vector<int> groups;
        for (int i = 0; i < n; ++i) {
            if (s[i] == '1') {
                int k = i;
                while (k + 1 < n && s[k+1] == '1') {
                    ++k;
                }
                if (k < n-1) {
                    groups.push_back(k-i+1);
                } 
                i = k;
            }
        }

        int m = groups.size();
        for (int i = 0; i < m; ++i) {
            res += groups[i] * (m-i);
        }
        return res;
    }
};
