class Solution {
public:
    int maxOperations(string s) {

        int w = 0;
        int ans = 0;

        int i=0;
        while(i<s.size()){

            if(s[i]=='1'){
                int a = 1;
                i++;
                while(i<s.size() && s[i]=='1'){
                    a++;
                    i++;
                }
                w+=a;
            }
            else if(s[i]=='0'){
                ans+=w;
                while(i<s.size() && s[i]=='0'){
                    i++;
                }


            }
        }

        return ans;

    }


};
