class Solution:
    def maxOperations(self, s: str) -> int:
        zero_count = 0
        N = len(s)
        ans = 0
        for i in range(N - 2, -1, -1):
            if s[i] == '1' and s[i+1] == '0':
                zero_count += 1
            if s[i] == '1':
                ans += zero_count;
        return ans
