class Solution:
    def maxOperations(self, s: str) -> int:        
        bunches = []
        bunchSize = 0

        for i in range(len(s)):
            if s[i] == "0":
                # end prev bunch
                if bunchSize == 0:
                    continue
                bunches.append(bunchSize)
                bunchSize = 0
            else:
                bunchSize += 1
        if bunchSize:
            bunches.append(bunchSize)

        if not bunches:
            return 0

        res = 0
        for i in range(len(bunches) - 1):
            res += bunches[i]
            bunches[i+1] += bunches[i]

        isNeedMoveLastBunch = s[-1] == "0"
        if isNeedMoveLastBunch:
            res += bunches[-1]
        return res
