class Solution {
public:
    int maxOperations(string s) {
        int sum = 0, cnt = 0, last = -1, n = s.size();
        for (int i = 0; i < n; i++)
        {
            if (s[i] == '1')
            {
                if (i - last > 1) sum += cnt;
                cnt++, last = i;
            }
        }
        if (last !=  n - 1) sum += cnt;
        return sum;
    }
};
