class Solution {
    fun maxOperations(s: String): Int {
        var count = 0;
        var ones = 0;
        for (i in 0..s.lastIndex - 1) {
            if (s[i] == '1') {
                ones++;
                if (s[i+1] == '0') count += ones;
            }
        }
        return count;
    }
}
