class Solution {
public:
    int maxOperations(string s) {
        int n = (int)s.size();
        int ptr = 0, cnt = 0;
        long long ans = 0;
        while (ptr < n) {
            // move until we find 1
            while (ptr < n && s[ptr] == '0') ptr++;
            // move the stuff
            ans += cnt;
            // move while in this chunk
            while (ptr < n && s[ptr] == '1') {
                ptr++; cnt++;
            }
        }
        return ans;
    }
};
