class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int curr = 0;
        int index = s.length()-1;

        while(index >= 0 && s[index] != '1')
            index--;
        if(index != s.length()-1 && index != -1){
            curr++;
            ans++;
        }
        //cout << index << '\n';
        for(int i = index-1; i >= 0; i--){
            if(s[i] == '1'){
                //cout << index << '\n';
                if(s[i+1] == '0')
                    curr++;
                ans += curr;
            }
        }
        return ans;
    }
};
