class Solution {
public:
    int maxOperations(string s) {
        vector<int> nums;
        if (s[0] == '1') {
            nums.push_back(1);
        }
        for (int i = 1; i < s.size(); ++i) {
            if (s[i] == '1') {
                if (s[i - 1] == '1') {
                    ++nums.back();
                } else {
                    nums.push_back(1);
                }
            }
        }

        int cnt = 0;
        int total = 0;
        for (int &num : nums) {
            cnt += num;
            total += cnt;
        }

        if (s.back() != '0') {
            total -= cnt;
        }
        return total;
    }
};
