class Solution {
public:
    int maxOperations(string s) {
        int lhs1 = 0;
        int ans = 0;
        bool pushed = false;
        for(int i=0; i<s.size(); i++){
            if(s[i] == '0' && !pushed){
                ans += lhs1;
                pushed = true;
            }
            if(s[i] == '1'){
                lhs1 += 1;
                pushed = false;
            }
        }
        return ans;
    }
};
