class Solution {
public:
    int maxOperations(string s) {
        s.push_back('1');
        int ans = 0;
        int cnt = 0;
        for (int i = 0; i < s.size(); i ++) {
            if (s[i] == '1' && i - 1 >= 0 && s[i - 1] == '0') {
                ans += cnt;
            }
            if (s[i] == '1') cnt ++;
        }
        return ans;
    }
};
