class Solution:
    def maxOperations(self, s: str) -> int:
        count_ones = 0
        steps = 0
        mul = 0
        prev = 1
        for i in range(len(s)-1, -1, -1):
            if s[i] == '0':
                if prev == 1:
                    mul+=1
                    prev = 0
            else:
                steps+=mul
                prev = 1
        return steps
