class Solution {
public:
    int maxOperations(string s) {
        vector<int> v;
        int i=0;
        while(i<s.size()){
            if(s[i]=='0') {
                ++i;
            }else{
            int count=0;
            while(i<s.size() && s[i]=='1'){
                ++i;
                ++count;
            }
            v.push_back(count);


            }
        }
        if(s[s.size()-1]=='1') v.pop_back();
        int res=0;
        int temp=0;
        for(int i=0;i<v.size();i++){
            res+= v[i]+temp;
            temp=v[i]+temp;
        }
        return res;
    }
};
