class Solution {
public:
    int maxOperations(string s) {
        int n=(int)s.size();
        vector<int> v;
        int cnt=0;
        for(int i=0;i<n;i++){
            if(s[i]=='1') cnt++;
            else{
                if(cnt>0) v.push_back(cnt);
                cnt=0;
            }
        }
        if(cnt>0) v.push_back(cnt);
        int ans=0,sum=0;
        for(int i=0;i<(int)v.size();i++){
            ans+=sum;
            sum+=v[i];
        }
        if(s.back()=='0') ans+=sum;
        return ans;
    }
};
