class Solution {
    public int maxOperations(String s) {
        int operations = 0;
        ArrayList<Integer> streaks = new ArrayList<Integer>();
        int curr = 0;
        for (int i = 0; i<s.length(); i++) {
            if (s.charAt(i) == '1') {
                curr++;
            } else {
                if (curr > 0) {
                    streaks.add(curr);
                }
                curr = 0;
            }
        }
        if (streaks.size() == 0) {
            return 0;
        }
        for (int i = 0; i<streaks.size() - 1; i++) {
            operations += streaks.get(i);
            streaks.set(i + 1, streaks.get(i + 1) + streaks.get(i));
        }
        operations += streaks.get(streaks.size() - 1);
        return operations;
    }
}
