class Solution {
public:
    int maxOperations(string s) {
        int ones = 0;
        int count = 0;
        bool prevzero = false;
        for (char i : s) {
            if (i == '0' && !prevzero) {count += ones; prevzero = true;}
            if (i == '1') {ones++; prevzero = false;}
        }
        return count;
    }
};
