class Solution {
public:
    int maxOperations(string s) {
        int countOfOne = 0;
        int ret = 0;
        bool encounterZero = false;

        for (auto c : s) {
            if (c == '1') {
                if (encounterZero) {
                    ret += countOfOne;
                    encounterZero = false;
                }
                countOfOne++;
            } else {
                encounterZero = true;
            }
        }

        if (encounterZero) {
            ret += countOfOne;
        }

        return ret;
    }
};
