class Solution {
public:
    int maxOperations(string s) {
        int ans = 0;
        int n = s.size();
        vector<int> nums;
        int sum = 0;
        for (int i = n - 1;i >= 0;i--) {
            if (s[i] == '1')
                sum++;
            else {
                if (sum > 0)
                    nums.push_back(sum);
                sum = 0;
            }
        }
        if (sum)
            nums.push_back(sum);
        reverse(nums.begin(), nums.end());
        if (s[n - 1] == '1')
            nums.pop_back();
        int m = nums.size();
        // for (int i : nums)
        //     cout << i << ' ';
        // cout << '\n';
        // cout << m << '\n';
        for (int i = 0;i < m;i++) {
            ans += nums[i];
            if (i + 1 < m)
                nums[i + 1] += nums[i];
        }
        return ans;
    }
};
