class Solution {
public:
    int maxOperations(string s) {
        vector<int> group;
        int ones = 0;
        for(auto letter : s){
            if(letter == '0'){
                if(ones > 0) group.push_back(ones);
                ones = 0;
            }else{
                ones++;
            }
        }
        for(int i=1;i<group.size();i++) group[i] += group[i-1];
        int operations=0;
        for(auto operation : group) operations += operation;
        return operations;
    }
};
