class Solution:
    def maxOperations(self, s: str) -> int:

        c = 0
        ret = 0
        i = 0
        l = len(s)
        while i < l:
            if s[i] == "1":
                c += 1

            # print(i, c, ret)
            if s[i] == '1' and i < len(s) - 1 and s[i+1] == "0":
                i += 1
                while i < l and s[i] == '0':
                    i += 1
                ret += c
            else:
                i += 1
            #print("af", i, s[i] if i < l else -1, c, ret)

        return ret
