class Solution {
    func maxOperations(_ s: String) -> Int {
        var totalOperations = 0
        var consecutiveOnes = 0
        var isCounting = true

        for char in s {
            if char == "1" {
                consecutiveOnes += 1
                isCounting = true
            } else {
                if isCounting {
                    totalOperations += consecutiveOnes
                }
                isCounting = false
            }
        }

        return totalOperations
    }
}
